import { Component, Input,OnInit } from '@angular/core';
import { Service } from 'src/app/Classes/Service/service';

@Component({
  selector: 'app-service',
  templateUrl: './service.component.html',
  styleUrls: ['./service.component.scss']
})

export class ServiceComponent implements OnInit{
@Input()
service:Service=new Service;

url="../../../assets/images/"
constructor(){}
ngOnInit(): void {

}

}
<div class="card" style="width:400px">
    <img class="card-img-top" src={{url}}{{service.image}} alt="Card image">
    <div class="card-body">
      <h4 class="card-title">{{service.name}}</h4>
      <p class="card-text">{{service.description}}</p>
      <p class="card-text">{{service.priceRange}}</p>
      <a routerLink="/more-detailes/{{service.id}}" class="btn btn-primary">more detailes</a>
