import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'shortTex'
})
export class ShortTexPipe implements PipeTransform {

  transform(value: string, ...args: unknown[]): string {
    let index;
    if(value.length>50){
      index=value.indexOf(" ",2);
      if(!index)
      index=50
     return value.substring(0,index)+'...';
    }
    else{
    index=value.length
    return value.substring(0,index)+'...';
    }

  }

}
