import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'shortTex'
})
export class ShortTexPipe implements PipeTransform {

  transform(value: string, ...args: unknown[]): string {
   return value.substring(0,2)+'...';
  }

}
