import { Component,OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Feedback } from 'src/app/Classes/feedback/feedback';
import { HttpService } from 'src/app/Servises/http.service';
import { SubjectService } from 'src/app/Servises/subject.service';
@Component({
  selector: 'app-add-feedback',
  templateUrl: './add-feedback.component.html',
  styleUrls: ['./add-feedback.component.scss']
})
export class AddFeedbackComponent implements OnInit{
  constructor(public http:HttpService,public router:Router,public subjectser:SubjectService){}
  ngOnInit(): void {

  }
  form=new FormGroup({
    name:new FormControl('',Validators.required),
    email:new FormControl('',Validators.required),
Telephone:new FormControl('',[Validators.required,Validators.minLength(9),Validators.minLength(10)]),
Comments:new FormControl('',[Validators.required,Validators.minLength(5)]),
  })
feedback:Feedback=new Feedback;
id:number=0
  onSubmit() {

    this.feedback.id=this.id++;
    this.feedback.Name=this.form.value.name!=null?this.form.value.name:" ";
    this.feedback.Content=this.form.value.Comments!=null?this.form.value.Comments:" ";
    this.feedback.Date=new Date();
    this.feedback.likes=0;
    this.subjectser.giftCard$.next("A gift card is sent to your email in these moments")
  this.http.saveFeedback(this.feedback).subscribe((data)=>{console.log(data)})
  this.router.navigate(['/feedbacks'])

  }
  commond(){

  }
}

<form action="/home" [formGroup]="form" (ngSubmit)="onSubmit()">
    <div id="parent">
    <div class="wrapper">
    <div class="mb-3">
        <label for="pwd" class="form-label">Name:</label>
        <input type="name" class="form-control" id="pwd" placeholder="Enter name"  formControlName="name" required>
      </div>
    <div class="mb-3 mt-3">
      <label for="email" class="form-label">Email:</label>
      <input type="email" class="form-control" id="email" placeholder="Enter email" name="email" formControlName="email">
    </div>
    <div class="mb-3">
        <label for="comment">Comments:</label>
         <textarea  class="form-control" rows="5" id="comment" name="text" formControlName="Comments" ></textarea>
    </div>
    <div class="mb-3">
        <label for="comment">Telephone:</label>
         <input appPhoneValid type="tel" class="form-control" id="Telephone" placeholder="Enter Telephone" name="Telephone" formControlName="Telephone">
    </div>

    <button type="submit" (submit)="commond()"(submit)="onSubmit()" class="btn btn-ligth" [disabled]="!form.valid">send</button>
</div>
</div>
  </form>
