import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'shortTex'
})
export class ShortTexPipe implements PipeTransform {

  transform(value: string, ...args: unknown[]): string {
    let index=value.indexOf(" ",2);
    if(!index)
    index=50
   return value.substring(0,index)+'...';
  }

}
